CREATE TABLE IF NOT EXISTS `char_templates` (
	`ClassID` tinyint(2) NOT NULL default '1',
	`Class_Name` varchar(15) NOT NULL default '',
	`Base_Health` tinyint(2) NOT NULL default '50',
	`Base_Prospection` smallint(4) NOT NULL default '100',
	`Weapon_Bonuses` text NOT NULL default '',
	`Agility_Levels` text NOT NULL default '',
	`Chance_Levels` text NOT NULL default '',
	`Intelligence_Levels` text NOT NULL default '',
	`Strengh_Levels` text NOT NULL default '',
	`Vitality_Levels` text NOT NULL default '',
	`Wisdom_Levels` text NOT NULL default '',
	PRIMARY KEY (`ClassID`)
);

